    public function getSourceContext()
    {
        return new Twig_Source("{% extends \"@App/finance_pages/index.html.twig\" %}


 {% block showUser %}
     <li>
         <a href=\"#\" class=\"dropdown-toggle\" data-toggle=\"dropdown\" role=\"button\" aria-haspopup=\"true\" aria-expanded=\"false\"> Welcome, {{ user.name }} <span class=\"caret\"></span></a>
         <ul class=\"dropdown-menu\">
             <li class=\"active\">
                 {% if user.image is empty %}
                     <img src=\"{{ asset('assets/person_images/default.png') }}\" width=\"100px\" class=\"img-rounded\">
                 {% else %}
                     <img src=\"{{ asset('assets/person_images/'~user.image) }}\" width=\"100px\" class=\"img-rounded\">
                 {% endif %}
             </li>
             <li role=\"separator\" class=\"divider\"></li>
             <li class=\"dropdown-header\">{{ user.type|upper }}</li>
         </ul>
     </li>
 {% endblock %}


{% block navbar %}
    <li class=\"active\"><a href=\"{{ path('finance') }}\">Home</a></li>
    <li><a href=\"{{ path('show_student')}}\">students</a></li>
    <li><a href=\"{{ path('profile')}}\">Profile</a></li>
    <li><a href=\"{{ path('add')}}\">Add Payment</a></li>
{% endblock %}


{% block breadcrumb %}
    <li>/<a href=\"{{ path('finance') }}\">Home</a></li>
    <li>/<a href=\"#\">Profile</a></li>
{% endblock %}

{% block page_title %}
    <h1><span class=\"glyphicon glyphicon-cog\" aria-hidden=\"true\"></span> Student Payments <small>Dorm Management System</small></h1>
{% endblock %}


{% block body %}


    {#<div class=\"top-button-group\">#}

        {#<a href=\"# \" class=\"button-style btn btn-success top-button\"><i class=\"glyphicon glyphicon-pencil\"></i> Edit</a>#}
    {#</div>#}



    <div id='msg-upload' class=\"hidden messages alert alert-danger\" role=\"alert\">
    </div>


    <div class=\"\">
        <div class=\"panel panel-default\">
            <div class=\"panel-heading main-color-bg\">

                <h3 class=\"panel-title\">User profile</h3>

            </div>
            <div class=\"panel-body\">

                <div class=\"row\">
                    <div class=\"col-md-4 text-center\">
                        <img class=\"img-circle avatar avatar-original\" style=\"-webkit-user-select:none;
  display:block; margin:auto;\"
                                {% if user.image is empty %}
                                    src=\"{{ asset('assets/person_images/default.png') }}\"
                                {% else %}
                                    src=\"{{ asset('assets/person_images/'~user.image) }}\"
                                {% endif %}
                        >
                    </div>
                    <div class=\"col-md-8\">
                        <div class=\"row\">
                            <div class=\"col-md-12\">
                                <h1 class=\"only-bottom-margin\">{{ user.name~\" \"~user.surname }}</h1>
                            </div>
                        </div>
                        <div class=\"row\">
                            <div class=\"col-md-6\">
